import React,{Component} from 'react';
import Field from '../InputField';
import targets from '../targets.json';
import './login.css';

class Login extends Component{
    constructor(){
        super();
        this.state={
            user:{
                email:'',
                password:''
            }
        }
    }
    componentDidMount(){

    }
    onEmailChange=(input)=>{
        let user=this.state.user;
        user.email=input.target.value;
        this.setState({user:user});
    }
    onPasswordChange=(input)=>{
        let user=this.state.user;
        user.password=input.target.value;
        this.setState({user:user});
    }
    onSumbit=()=>{
        fetch('http://localhost:3000/signin',{
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body:JSON.stringify(this.state.user)
        })
        .then(response=>{
            if(response.status===200)
            alert('signed in');
            else
                alert('email and password invalid');
        })
        .catch(err=>console.log(err));
    }
    render(){
         const {onEmailChange,onPasswordChange,onSumbit}=this;
        return(<form method="POST" action={targets.signin} href="#" target="/" className="center measure ">
            <legend className="f4 fw6 ph0 mh0">Sign In</legend>
            <Field action={onEmailChange} type="email" id="email" name="Email"/>
            <Field action={onPasswordChange} type="password" id="password" name="Password"/>
            <div className="mt3">
                <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
                type="reset"
                onClick={onSumbit}
                value="Sign in"
                />
            </div>
            <div className="lh-copy mt3">
                <a href="#0" className="f6 link dim black db">Sign up</a>
            </div>
        </form>);
    }
}
export default Login;
