import React,{Component} from 'react';
import {
  Switch,
  Route,
  Link
} from "react-router-dom";
import targets from './targets.json';
import Login from './login';
import Register from './register';
import Accounts from './Accounts';
import Profile from './Profile';
import Nav from './Nav';
import Password from './Password';
import Remove from './Remove';
import './App.css';
import 'tachyons';

class App extends Component {
  constructor(){
    super();
    this.state={
      user:{
        firstName:'',
        lastName:'',
        email:'',
        birthday:'',
        gender:'',
        password:''
    },
    login:false,
    users:[],
    genders:[]

  }
  }
  updateUser=(field,value)=>{
    let user=this.state.user;
        switch(field){
            case 'firstName':
                user.firstName=value;
                break;
                case 'lastName':
                    user.lastName=value;
                break;
                case 'email':
                    user.email=value;
                break;
                case 'birthday':
                    user.birthday=value;
                break;
                case 'gender':
                    user.gender=value;
                break;
                case 'password':
                    user.password=value;
                break;
        }
        this.setState({user:user});
  }
  onChange=(input)=>{
    console.log(input.target.id,input.target.value);
    this.updateUser(input.target.id,input.target.value);
  }
  onLogIn=()=>{
    fetch(targets.signin,{
      method:"POST",
      headers:{"Content-Type":"application/json"},
      body:JSON.stringify(this.state.user)
  })
  .then(response=>{
      if(response.status===200){
          alert('Sign In');
          this.setState({login:true});
      }
      else
          alert('Failed');
  })
  .catch(err=>console.log(err));
  }
  render(){
    const {onChange,onLogIn}=this;
    const {login}=this.state;
      return (
      <div className="App">
        <nav class="ph3 ph5-ns pv4 w-100">
  <div class="nowrap overflow-x-auto">

        <Nav link="signin" name="Sign In"/>
        <Nav link="signup" name="Sign Up"/>
        <Nav link="users" name="Active Users"/>
        <Nav link="profile" name="Profile"/>
        </div>
          </nav>
        <Switch>
          <Route path="/signin">
            <Login onChange={onChange} onSubmit={onLogIn}/>
          </Route>
          <Route path="/signup">
            <Register/>
          </Route>
          <Route path="/users">
            <Accounts/>
          </Route>
          <Route path="/profile">
            <Profile/>
            <br/>
            <Password/>
            <br/>
            <Remove/>
            <br/>
          </Route>
        </Switch>

      </div>
    );
    }
}

export default App;
