import React,{Component} from 'react';
import Field from '../Field';
import targets from '../targets.json';
import {Link } from 'react-router-dom';
import './login.css';

class Login extends Component{
    constructor(){
        super();
        this.state={
            user:{
                email:'',
                password:''
            }
        }
    }
    updateUser=(field,value)=>{
        let user=this.state.user;
        switch(field){
            case 'email':
                user.email=value;
                break;
            case 'password':
                user.password=value;
                break;
        }
        
        this.setState({user:user});
    }
    onChange=(input)=>{
       this.updateUser(input.target.id,input.target.value);
    }
    onSubmit=()=>{
        fetch(targets.signin,{
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body:JSON.stringify(this.state.user)
        })
        .then(response=>{
            if(response.status===200)
            alert('signed in');
            else
                alert('email and password invalid');
        })
        .catch(err=>console.log(err));
    }
 
    render(){
         const {onChange,onSubmit}=this;
        return(<form method="POST" action={targets.signin} href="#" target="/" className="center measure ">
            <legend className="f4 fw6 ph0 mh0">Sign In</legend>
            <Field action={onChange} type="email" id="email" name="Email"/>
            <Field action={onChange} type="password" id="password" name="Password"/>
            <div className="mt3">
                <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
                type="reset"
                onClick={onSubmit}
                value="Sign in"
                />
            </div>
            <div className="lh-copy mt3">
            
                <lavel href="/signup" className="f6 link dim black db">
                    Sign up
                    </lavel>
            </div>
        </form>);
    }
}
export default Login;
