import React,{Component} from 'react';
import Field from '../Field';
import targets from '../targets.json';
class Register extends Component{
    constructor(){
        super();
        this.state={
            user:{
                firstName:'',
                lastName:'',
                email:'',
                birthday:Date.now(),
                gender:'',
                password:''
            },
            genders:[]
        }
        
    }
    componentDidMount(){
        fetch(targets.genders)
        .then(response=>response.json())
        .then(data=>{
            this.setState({genders:data})
        });
    }
    updateUser=(field,value)=>{
        let user=this.state.user;
        switch(field){
            case 'firstName':
                user.firstName=value;
                break;
                case 'lastName':
                    user.lastName=value;
                break;
                case 'email':
                    user.email=value;
                break;
                case 'birthday':
                    user.birthday=value;
                break;
                case 'gender':
                    user.gender=this.state.genders[value].gender;
                break;
                case 'password':
                    user.password=value;
                break;
        }
        this.setState({user:user});
    } 
    onChange=(input)=>{
        console.log(input.target.id,input.target.value);
        this.updateUser(input.target.id,input.target.value);
    }
    onSubmit=()=>{
        fetch(targets.signup,{
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body:JSON.stringify(this.state.user)
        })
        .then(response=>{
            if(response.status===200)
                alert('Sign up');
            else
                alert('Failed');
        })
        .catch(err=>console.log(err));
    }
    render(){
        const {onChange,onSubmit}=this;
        const {genders}=this.state;
        const list=genders.map((item,index)=> 
        <option label={item.gender} value={index}>
            {item.gender}
        </option>)
        return(
        <form method="POST" action={targets.signin} href="#" target="/" className="center measure ">
            <legend className="f4 fw6 ph0 mh0">Sign Up</legend>
            <Field action={onChange} type="email" id="email" name="Email"/>
            <Field action={onChange} type="firstname" id="firstName" name="First Name"/>
            <Field action={onChange} type="lastname" id="lastName" name="Last Name"/>
            <Field action={onChange} type="date" id="birthday" name="Birthday"/>
            <select id="gender"
             className="w-100  mt3 db h2 f6 bg-near-white ba b--sliver gray" 
             onClick={onChange}
             >
            {list}
            </select>
            <Field action={onChange} type="password" id="password" name="Password"/>
            <div className="mt3">
                <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
                type="reset"
                onClick={onSubmit}
                value="Sign up"
                />
            </div>
            <div className="lh-copy mt3">
                <a href="/signin" className="f6 link dim black db">Sign in</a>
            </div>
    </form>);
    }
}
export default Register;
