import React,{Component} from 'react';
import Field from '../Field';
import targets from '../targets.json';
import {SignIn,Fill} from '../redux/actions';
import {useDispatch,useSelector} from 'react-redux';
import './login.css';

const Login =()=>{
   /*constructor(){
        super();
        this.state={
            user:{
                email:'',
                password:''
            }
        }
    }
    updateUser=(field,value)=>{
        let user=this.state.user;
        switch(field){
            case 'email':
                user.email=value;
                break;
            case 'password':
                user.password=value;
                break;
        }
        
        this.setState({user:user});
    }
    
    onSubmit=()=>{
        fetch(targets.signin,{
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body:JSON.stringify(this.state.user)
        })
        .then(response=>{
            if(response.status===200)
            alert('signed in');
            else
                alert('email and password invalid');
        })
        .catch(err=>console.log(err));
    }*/
         //const {onChange,onSubmit}=this;
         const onChange=(input)=>{
             console.log(input.target.id,input.target.value);
            dispatch(Fill(input.target.id,input.target.value));
         }
        const dispatch=useDispatch();
        const user =useSelector(state=>state.Fill);
        return(<div className="center measure ">
            <legend className="f4 fw6 ph0 mh0">Sign In</legend>
            <Field action={onChange} type="email" id="email" name="Email"/>
            <Field action={onChange} type="password" id="password" name="Password"/>
            <div className="mt3">
                <button className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
                onClick={()=>dispatch(SignIn(user))}
                >Sign in</button>
            </div>
            <div className="lh-copy mt3">
                <a href="/signup" className="f6 link dim black db">
                    Sign up
                    </a>
            </div>
        </div>);
    
}
export default Login;
